package cal;

public class If {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 200;
		int num2 = 100;
		int result = num1 + num2;
		
		
		if(result >= num1 + num2) {
			System.out.println(result - 100);
		}else {
			System.out.println(result * 0.1);
		}

	}

}
